package java0804;

public class Ex_02증감연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num = 0;
		num += 3;
		
		System.out.println(num);
		System.out.println(num++); //3 숫자먼저 출력, 값은 더해진상태
		System.out.println(++num); //5 4가 된 위에값에서 1이 더해져 출력
		
		//byte num1 = 127;
		
		//System.out.println(num1);

	}

}
