package java0804;

public class Ex_03비교연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		//비교연산자 : 부등호 연산, 같다/다르다 연산
		// <, <=, >, >=, ==, !=
		
		int num1 = 3;
		int num2 = 10;
		
		System.out.println(num1== num2);
		System.out.println(num1 != num2);
		System.out.println(num1 > num2);
		System.out.println(num1 <= num2);
		
		
	}

}
