package java0810;

public class 구구단이단부터구단 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int x=2; x<10; x++) {
			System.out.println("------"+x+"단------");
		for(int i=1; i<10; i++) {
			System.out.println(" "+x+"*"+i+"="+x*i);
			

		}//System.out.println();
	}
	}
}
