package java0811;

public class Ex13_보충7번for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 21; i < 58; i++) {
			System.out.print(i + " ");
		}
		System.out.println();
		for (int j = 96; j > 52; j--) {
			System.out.print(j + " ");
		}
		System.out.println();
		for (int k = 21; k < 58; k += 2) {
			System.out.print(k + " ");
		}

	}
}